mapper.select:
SELECT COUNT(*) as cnt FROM contents
WHERE bookno=#{bookno}
WHERE bookno=#{bookno} AND (title LIKE '%' || #{word} || '%' OR content LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%')

SELECT contentsno, bookno, memberno, title, content, recom, word, rdate, file1,file1saved,thumb1,size1,price,saleprice,dc,point,writer,publisher FROM ( SELECT contentsno, bookno, memberno, title, content, recom, word, rdate, file1,file1saved,thumb1,size1,price,saleprice,dc,point,writer,publisher, rownum as r FROM ( SELECT contentsno, bookno, memberno, title, content, recom, word, rdate, file1,file1saved,thumb1,size1,price,saleprice,dc,point,writer,publisher FROM contents
WHERE bookno=#{bookno}
WHERE (title LIKE '%' || #{word} || '%' OR content LIKE '%' || #{word} || '%' OR publisher LIKE '%' || #{word} || '%' OR writer LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%')
ORDER BY contentsno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}
